  const [deletingId, setDeletingId] = useState<string | null>(null);
  const [refreshing, setRefreshing] = useState(false);
  const [viewMode, setViewMode] = useState<'table' | 'grid'>('grid');
  // Bi·∫øn ƒë·ªÉ chuy·ªÉn ƒë·ªïi Location sang ƒë·ªãnh d·∫°ng GridViewItem
  const mapLocationsToGridItems = (locations: LocationData[]) => {
    return locations.map((location) => ({
      id: location.id,
      title: location.code,
      subtitle: location.subcategory,
      avatarText: location.avatar,
      status: location.status === 'active' ? 'Ho·∫°t ƒë·ªông' : 'T·∫°m d·ª´ng',
      details: [
        {
          label: 'Danh m·ª•c',
          value: location.category,
          color: '#4a90e2',
        },
        {
          label: 'ƒê·ªãa ch·ªâ',
          value: location.address,
          color: '#50b46e',
        },
        {
          label: 'Khu v·ª±c',
          value: `${location.ward}, ${location.district}, ${location.province}`,
          color: '#ff9966',
        },
      ],
      actions: [
        {
          label: 'Ch·ªânh s·ª≠a',
          icon: <Edit fontSize="small" />,
          onClick: (item: GridViewItem) => {
            setEditing(locations.find((loc) => loc.id === item.id) || null);
            setOpen(true);
          },
          color: 'primary',
        },
        {
          label: 'X√≥a',
          icon: <Delete fontSize="small" />,
          onClick: (item: GridViewItem) => {
            handleDelete(locations.find((loc) => loc.id === item.id)!);
          },
          color: 'error',
        },
      ],
    }));
  };
  const [showAllGrid, setShowAllGrid] = useState(false);
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    province: '',
    district: '',
    ward: '',
    category: '',
    status: '',
    search: '',
  });

  // Load t·ª´ Google Sheets
  useEffect(() => {
    const loadLocations = async () => {
      setLoading(true);
      try {
        const res = await fetch(
          `/api/locations?spreadsheetId=${encodeURIComponent(SHEET_ID)}`
        );
        if (res.ok) {
          const data = await res.json();
          const mapped: LocationData[] = (
            data as Array<Record<string, unknown>>
          ).map((r) => ({
            id: getStringFrom(r, 'id'),
            code: getStringFrom(r, 'code'),
            avatar: getStringFrom(r, 'avatar'),
            category: getStringFrom(r, 'category'),
            subcategory: getStringFrom(r, 'subcategory'),
            address: getStringFrom(r, 'address'),
            status: getStringFrom(r, 'status') as 'active' | 'inactive',
            ward: getStringFrom(r, 'ward'),
            district: getStringFrom(r, 'district'),
            province: getStringFrom(r, 'province'),
            note: getStringFrom(r, 'note'),
          }));
          setLocations(mapped);
        } else {
          setLocations(mockLocations);
        }
      } catch {
        setError('L·ªói t·∫£i danh s√°ch ƒë·ªãa ƒëi·ªÉm');
        setLocations(mockLocations);
      } finally {
        setLoading(false);
      }
    };

    loadLocations();
  }, []);

  // S·∫Øp x·∫øp theo Th√†nh ph·ªë ‚Üí Qu·∫≠n ‚Üí Ph∆∞·ªùng
  const sortedLocations =
    locations?.sort((a, b) => {
      // S·∫Øp x·∫øp theo province tr∆∞·ªõc
      if (a.province !== b.province) {
        return a.province.localeCompare(b.province);
      }
      // N·∫øu c√πng province, s·∫Øp x·∫øp theo district
      if (a.district !== b.district) {
        return a.district.localeCompare(b.district);
      }
      // N·∫øu c√πng district, s·∫Øp x·∫øp theo ward
      return a.ward.localeCompare(b.ward);
    }) || [];

  // L·ªçc d·ªØ li·ªáu
  const filteredLocations = sortedLocations.filter((location) => {
    const matchesProvince =
      !filters.province || location.province.toLowerCase().includes(filters.province.toLowerCase());
    const matchesDistrict =
      !filters.district || location.district.toLowerCase().includes(filters.district.toLowerCase());
    const matchesWard = !filters.ward || location.ward.toLowerCase().includes(filters.ward.toLowerCase());
    const matchesCategory =
      !filters.category || location.category.toLowerCase().includes(filters.category.toLowerCase());
    const matchesStatus = !filters.status || location.status === filters.status;
    const matchesSearch =
      !filters.search ||
      location.code.toLowerCase().includes(filters.search.toLowerCase()) ||
      location.subcategory
        .toLowerCase()
        .includes(filters.search.toLowerCase()) ||
      location.address.toLowerCase().includes(filters.search.toLowerCase());

    return (
      matchesProvince &&
      matchesDistrict &&
      matchesWard &&
      matchesCategory &&
      matchesStatus &&
      matchesSearch
    );
  });

  // Columns for DataTable
  const tableColumns = [
    {
      id: 'code',
      label: 'ƒê·ªãa ƒëi·ªÉm',
      width: '25%',
      render: (_v: unknown, location: LocationData) => {
        const isActive = location.status === 'active';
        return (
          <Box display="flex" alignItems="center" gap={1.5}>
            <Avatar
              sx={{
                width: 32,
                height: 32,
                bgcolor: isActive ? 'primary.main' : 'grey.400',
                fontSize: '1rem',
              }}
            >
              {location.avatar}
            </Avatar>
            <Box sx={{ minWidth: 0, flex: 1 }}>
              <Typography variant="body2">{location.code}</Typography>
              <Typography variant="caption" color="text.secondary">
                {location.subcategory}
              </Typography>
            </Box>
          </Box>
        );
      },
    },
    { id: 'category', label: 'Danh m·ª•c', width: '15%' },
    {
      id: 'address',
      label: 'ƒê·ªãa ch·ªâ',
      width: '35%',
      render: (_v: unknown, location: LocationData) => (
        <Box>
          <Typography
            variant="body2"
            sx={{
              maxWidth: 150,
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              whiteSpace: 'nowrap',
            }}
          >
            {location.address || '-'}
          </Typography>
          <Typography variant="caption" color="text.secondary">
            {location.ward}, {location.district}, {location.province}
          </Typography>
        </Box>
      ),
    },
    {
      id: 'status',
      label: 'Tr·∫°ng th√°i',
      width: '12%',
      align: 'center',
      render: (_v: unknown, location: LocationData) => (
        <Chip
          label={location.status === 'active' ? 'Ho·∫°t ƒë·ªông' : 'T·∫°m d·ª´ng'}
          color={location.status === 'active' ? 'success' : 'default'}
          size="small"
          onClick={() => handleToggleStatus(location)}
          disabled={togglingId === location.id}
        />
      ),
    },
    {
      id: 'id',
      label: 'Thao t√°c',
      width: '13%',
      align: 'center' as const,
      render: (_v: unknown, location: LocationData) => (
        <Box>
          <IconButton
            size="small"
            onClick={() => {
              setEditing(location);
              setOpen(true);
            }}
          >
            <Edit />
          </IconButton>
          <IconButton
            size="small"
            color="error"
            onClick={() => handleDelete(location)}
            disabled={deletingId === location.id}
          >
            <Delete />
          </IconButton>
        </Box>
      ),
    },
  ];

  const handleToggleStatus = async (location: LocationData) => {
    setTogglingId(location.id);
    try {
      const newStatus = location.status === 'active' ? 'inactive' : 'active';
      const updatedLocation = {
        ...location,
        status: newStatus as 'active' | 'inactive',
      };

      const res = await fetch(
        `/api/locations/${location.id}?spreadsheetId=${encodeURIComponent(SHEET_ID)}`,
        {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(updatedLocation),
        }
      );

      if (res.ok) {
        setLocations((prev) =>
          prev.map((loc) => (loc.id === location.id ? updatedLocation : loc))
        );
      }
    } catch (err) {
      console.error('L·ªói c·∫≠p nh·∫≠t tr·∫°ng th√°i:', err);
    } finally {
      setTogglingId(null);
    }
  };

  // X·ª≠ l√Ω chuy·ªÉn ƒë·ªïi tr·∫°ng th√°i trong GridView
  const handleGridStatusToggle = (item: GridViewItem) => {
    const location = locations.find((loc) => loc.id === item.id);
    if (location) {
      handleToggleStatus(location);
    }
  };

  const handleDelete = async (location: LocationData) => {
    if (!window.confirm(`B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ƒë·ªãa ƒëi·ªÉm "${locations.code}"?`)) {
      return;
    }

    setDeletingId(location.id);
    try {
      const res = await fetch(
        `/api/locations/${location.id}?spreadsheetId=${encodeURIComponent(SHEET_ID)}`,
        { method: 'DELETE' }
      );

      if (res.ok) {
        setLocations((prev) => prev.filter((loc) => loc.id !== location.id));
      }
    } catch (err) {
      console.error('L·ªói x√≥a ƒë·ªãa ƒëi·ªÉm:', err);
    } finally {
      setDeletingId(null);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    try {
      const res = await fetch(
        `/api/locations?spreadsheetId=${encodeURIComponent(SHEET_ID)}`
      );
      if (res.ok) {
        const data = await res.json();
        const mapped: LocationData[] = (data as Array<Record<string, unknown>>).map(
          (r) => ({
            id: getStringFrom(r, 'id'),
            code: getStringFrom(r, 'code'),
            avatar: getStringFrom(r, 'avatar'),
            category: getStringFrom(r, 'category'),
            subcategory: getStringFrom(r, 'subcategory'),
            address: getStringFrom(r, 'address'),
            status: getStringFrom(r, 'status') as 'active' | 'inactive',
            ward: getStringFrom(r, 'ward'),
            district: getStringFrom(r, 'district'),
            province: getStringFrom(r, 'province'),
            note: getStringFrom(r, 'note'),
          })
        );
        setLocations(mapped);
      }
    } catch (err) {
      console.error('L·ªói l√†m m·ªõi:', err);
    } finally {
      setRefreshing(false);
    }
  };

  return (
    <Box>
      <Box
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        mb={3}
      >
        <Typography variant="h4">
          ƒê·ªãa ƒëi·ªÉm l∆∞u ({filteredLocations.length})
        </Typography>
        <Box>
          <Button
            variant="outlined"
            onClick={() => setShowFilters(!showFilters)}
            startIcon={<FilterList />}
            sx={{ mr: 1 }}
          >
            B·ªô l·ªçc
          </Button>
          <Button
            variant="outlined"
            onClick={handleRefresh}
            startIcon={refreshing ? <CircularProgress size={20} /> : <Sync />}
            disabled={refreshing}
            sx={{ mr: 1 }}
          >
            L√†m m·ªõi
          </Button>
          <Button
            variant="outlined"
            onClick={() => setViewMode(viewMode === 'table' ? 'grid' : 'table')}
            startIcon={viewMode === 'table' ? <GridView /> : <ViewList />}
            sx={{ mr: 1 }}
          >
            {viewMode === 'table' ? 'L∆∞·ªõi' : 'B·∫£ng'}
          </Button>
          <Button
            variant="contained"
            startIcon={<AddCircle />}
            onClick={() => {
              setEditing(null);
              setOpen(true);
            }}
          >
            Th√™m ƒë·ªãa ƒëi·ªÉm
          </Button>
        </Box>
      </Box>

      {/* B·ªô l·ªçc */}
      <Collapse in={showFilters}>
        <Paper sx={{ p: 3, mb: 3 }}>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            mb={2}
          >
            <Typography variant="h6">B·ªô l·ªçc</Typography>
            <Button
              variant="outlined"
              size="small"
              onClick={() =>
                setFilters({
                  province: '',
                  district: '',
                  ward: '',
                  category: '',
                  status: '',
                  search: '',
                })
              }
              startIcon={<Clear />}
            >
              X√≥a b·ªô l·ªçc
            </Button>
          </Box>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6} md={2}>
              <TextField
                fullWidth
                size="small"
                label="T√¨m ki·∫øm"
                value={filters.search}
                onChange={(e) =>
                  setFilters((prev) => ({ ...prev, search: e.target.value }))
                }
                placeholder="M√£, t√™n, ƒë·ªãa ch·ªâ..."
              />
            </Grid>
            <Grid item xs={12} sm={6} md={2}>
              <FormControl fullWidth size="small">
                <InputLabel>T·ªânh/Th√†nh ph·ªë</InputLabel>
                <Select
                  value={filters.province}
                  onChange={(e) =>
                    setFilters((prev) => ({
                      ...prev,
                      province: e.target.value,
                    }))
                  }
                  label="T·ªânh/Th√†nh ph·ªë"
                >
                  <MenuItem value="">T·∫•t c·∫£</MenuItem>
                  {Array.from(
                    new Set(locations?.map((l) => l.province) || [])
                  ).map((province) => (
                    <MenuItem key={province} value={province}>
                      {province}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6} md={2}>
              <FormControl fullWidth size="small">
                <InputLabel>Qu·∫≠n/Huy·ªán</InputLabel>
                <Select
                  value={filters.district}
                  onChange={(e) =>
                    setFilters((prev) => ({
                      ...prev,
                      district: e.target.value,
                    }))
                  }
                  label="Qu·∫≠n/Huy·ªán"
                >
                  <MenuItem value="">T·∫•t c·∫£</MenuItem>
                  {Array.from(
                    new Set(locations?.map((l) => l.district) || [])
                  ).map((district) => (
                    <MenuItem key={district} value={district}>
                      {district}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6} md={2}>
              <FormControl fullWidth size="small">
                <InputLabel>Ph∆∞·ªùng/X√£</InputLabel>
                <Select
                  value={filters.ward}
                  onChange={(e) =>
                    setFilters((prev) => ({ ...prev, ward: e.target.value }))
                  }
                  label="Ph∆∞·ªùng/X√£"
                >
                  <MenuItem value="">T·∫•t c·∫£</MenuItem>
                  {Array.from(new Set(locations?.map((l) => l.ward) || [])).map(
                    (ward) => (
                      <MenuItem key={ward} value={ward}>
                        {ward}
                      </MenuItem>
                    )
                  )}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6} md={2}>
              <FormControl fullWidth size="small">
                <InputLabel>Danh m·ª•c</InputLabel>
                <Select
                  value={filters.category}
                  onChange={(e) =>
                    setFilters((prev) => ({
                      ...prev,
                      category: e.target.value,
                    }))
                  }
                  label="Danh m·ª•c"
                >
                  <MenuItem value="">T·∫•t c·∫£</MenuItem>
                  {Array.from(
                    new Set(locations?.map((l) => l.category) || [])
                  ).map((category) => (
                    <MenuItem key={category} value={category}>
                      {category}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6} md={2}>
              <FormControl fullWidth size="small">
                <InputLabel>Tr·∫°ng th√°i</InputLabel>
                <Select
                  value={filters.status}
                  onChange={(e) =>
                    setFilters((prev) => ({ ...prev, status: e.target.value }))
                  }
                  label="Tr·∫°ng th√°i"
                >
                  <MenuItem value="">T·∫•t c·∫£</MenuItem>
                  <MenuItem value="active">Ho·∫°t ƒë·ªông</MenuItem>
                  <MenuItem value="inactive">T·∫°m d·ª´ng</MenuItem>
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </Paper>
      </Collapse>

      {loading ? (
        <Box
          display="flex"
          justifyContent="center"
          alignItems="center"
          minHeight={200}
        >
          <CircularProgress />
        </Box>
      ) : error ? (
        <Paper sx={{ p: 3 }}>
          <Typography color="error">{error}</Typography>
        </Paper>
      ) : locations && locations.length > 0 ? (
        viewMode === 'table' ? (
          <DataTable
            columns={tableColumns}
            data={filteredLocations}
            rowsPerPageOptions={[5, 10, 25, 50]}
            defaultRowsPerPage={10}
            emptyMessage="Ch∆∞a c√≥ ƒë·ªãa ƒëi·ªÉm n√†o"
            getRowId={(row) => row.id}
            title={`Danh s√°ch ƒë·ªãa ƒëi·ªÉm (${filteredLocations.length})`}
          />
        ) : (
          <>
            {/* Grid View - S·ª≠ d·ª•ng component GridView chung */}
            <Box>
              <GridView
                items={mapLocationsToGridItems(
                  showAllGrid
                    ? filteredLocations
                    : filteredLocations.slice(0, 8)
                )}
                gridSpacing={3}
                maxItemsPerRow={{ xs: 12, sm: 6, md: 4, lg: 3, xl: 3 }}
                onStatusToggle={handleGridStatusToggle}
                emptyMessage="Ch∆∞a c√≥ ƒë·ªãa ƒëi·ªÉm n√†o"
                emptyAction={{
                  label: 'Th√™m ƒë·ªãa ƒëi·ªÉm',
                  onClick: () => {
                    setEditing(null);
                    setOpen(true);
                  },
                  icon: <AddCircle />,
                }}
                showExpandableDetails={true}
              />
            </Box>

            {/* Xem th√™m section */}
            {filteredLocations.length > 8 && !showAllGrid && (
              <Box
                sx={{
                  display: 'flex',
                  justifyContent: 'center',
                  mt: 4,
                  pb: 2,
                }}
              >
                <Button
                  variant="outlined"
                  size="large"
                  onClick={() => setShowAllGrid(true)}
                  sx={{
                    px: 4,
                    py: 1.5,
                    borderRadius: 2,
                    borderColor: 'primary.main',
                    color: 'primary.main',
                    '&:hover': {
                      backgroundColor: 'primary.50',
                      borderColor: 'primary.dark',
                    },
                  }}
                >
                  Xem th√™m ({filteredLocations.length - 8} ƒë·ªãa ƒëi·ªÉm)
                </Button>
              </Box>
            )}

            {/* Thu g·ªçn section */}
            {showAllGrid && filteredLocations.length > 8 && (
              <Box
                sx={{
                  display: 'flex',
                  justifyContent: 'center',
                  mt: 4,
                  pb: 2,
                }}
              >
                <Button
                  variant="outlined"
                  size="large"
                  onClick={() => setShowAllGrid(false)}
                  sx={{
                    px: 4,
                    py: 1.5,
                    borderRadius: 2,
                    borderColor: 'grey.500',
                    color: 'grey.700',
                    '&:hover': {
                      backgroundColor: 'grey.50',
                      borderColor: 'grey.600',
                    },
                  }}
                >
                  Thu g·ªçn
                </Button>
              </Box>
            )}
          </>
        )
      ) : (
        <Paper sx={{ p: 4, textAlign: 'center' }}>
          <Typography variant="h6" color="text.secondary" mb={2}>
            Ch∆∞a c√≥ ƒë·ªãa ƒëi·ªÉm n√†o
          </Typography>
          <Button
            variant="contained"
            startIcon={<AddCircle />}
            onClick={() => {
              setEditing(null);
              setOpen(true);
            }}
          >
            Th√™m ƒë·ªãa ƒëi·ªÉm ƒë·∫ßu ti√™n
          </Button>
        </Paper>
      )}

      <CreateLocationDialog
        open={open}
        onClose={() => setOpen(false)}
        editing={editing}
        onSuccess={() => {
          setOpen(false);
          handleRefresh();
        }}
      />
    </Box>
  );
};

export default Locations;
